            'content'     => "<!-- wp:acf/homepage-hero {\"id\":\"block_61b9b4a298f59\",\"name\":\"acf/homepage-hero\",\"data\":{\"pre_title\":\"Elevating employee engagement\",\"_pre_title\":\"field_61b89fe6f20a2\",\"title\":\"Where your \\u003cbr/\\u003eteam belongs.\",\"_title\":\"field_61b89fedf20a3\",\"content\":\"Vityl cracks the code on company culture, cultivating belonging and fostering ongoing employee engagement to build more connected, collaborative, and successful workplaces.\",\"_content\":\"field_61b89ff6f20a4\",\"link\":{\"title\":\"SEE WHAT MAKES US VITYL\",\"url\":\"http://vityl.wunderdogs.xyz/platform/\",\"target\":\"\"},\"_link\":\"field_61b8a00af20a5\",\"image_1\":18,\"_image_1\":\"field_61b8a021f20a6\",\"image_2\":17,\"_image_2\":\"field_61b8a035f20a8\"},\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-platform {\"id\":\"block_61bd66073660f\",\"name\":\"acf/vityl-platform\",\"data\":{\"pre_title\":\"THE PLATFORM\",\"_pre_title\":\"field_61bd65b89a509\",\"title\":\"The culture catalyst \\u003cbr/\\u003efor modern businesses.\",\"_title\":\"field_61bd65c19a50a\",\"content\":\"Belonging is critical to a thriving workplace, but few organizations know how to assess, advance, and sustain it. \\u003cstrong\\u003eEnter Vityl, your roadmap to happier people and higher performance.\\u003c/strong\\u003e\",\"_content\":\"field_61bd65c89a50b\",\"link\":{\"title\":\"Explore Vityl features\",\"url\":\"http://vityl.wunderdogs.xyz/platform/#vityl-work\",\"target\":\"\"},\"_link\":\"field_61bd65df52fad\",\"list_0_title\":\"Surveys\",\"_list_0_title\":\"field_61bda9876e570\",\"list_0_content\":\"To assess your organization’s belonging\",\"_list_0_content\":\"field_61bda9906e571\",\"list_0_image\":1656,\"_list_0_image\":\"field_61bda99a6e572\",\"list_1_title\":\"Measurable goals\",\"_list_1_title\":\"field_61bda9876e570\",\"list_1_content\":\"That enhance your unique \\u003cbr/\\u003eculture\",\"_list_1_content\":\"field_61bda9906e571\",\"list_1_image\":1657,\"_list_1_image\":\"field_61bda99a6e572\",\"list_2_title\":\"Tailored nudges\",\"_list_2_title\":\"field_61bda9876e570\",\"list_2_content\":\"To bring your teams \\u003cbr/\\u003etogether\",\"_list_2_content\":\"field_61bda9906e571\",\"list_2_image\":1658,\"_list_2_image\":\"field_61bda99a6e572\",\"list\":3,\"_list\":\"field_61bda9796e56f\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-science {\"id\":\"block_61bde4ca3385d\",\"name\":\"acf/vityl-science\",\"data\":{\"pre_title\":\"THE SCIENCE\",\"_pre_title\":\"field_61bde479fdd3b\",\"title\":\"Leveraging research for real change.\",\"_title\":\"field_61bde486fdd3c\",\"content\":\"We’ve done the work so you don’t have to. Developed by a team of HR specialists and academic leaders shaping the culture of world-class companies, Vityl’s data-driven employee engagement software is informed by industry best practices and a scientific model of belonging.\",\"_content\":\"field_61bde48efdd3d\",\"link\":{\"title\":\"Go behind the scenes\",\"url\":\"http://vityl.wunderdogs.xyz/belonging/\",\"target\":\"\"},\"_link\":\"field_61bde49afdd3e\",\"image\":114,\"_image\":\"field_61bde4a5fdd3f\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-latest-news {\"id\":\"block_61bdf2d530fbe\",\"name\":\"acf/vityl-latest-news\",\"data\":{\"pre_title\":\"LATEST NEWS\",\"_pre_title\":\"field_61bdf2bd66ab7\",\"link\":{\"title\":\"Resources\",\"url\":\"http://vityl.wunderdogs.xyz/resources/\",\"target\":\"\"},\"_link\":\"field_61ddb2c7a4682\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-action-cta {\"id\":\"block_61bea5ac2cf11\",\"name\":\"acf/vityl-action-cta\",\"data\":{\"title\":\"See Vityl in action.\",\"_title\":\"field_61bea4d299143\",\"content\":\"Take our toolkit for a test drive to get a glimpse of Vityl’s impact on employee engagement.\",\"_content\":\"field_61bea4d899144\",\"link\":{\"title\":\"WATCH demo\",\"url\":\"#video\",\"target\":\"\"},\"_link\":\"field_61bea4e399145\"},\"align\":\