    register_block_pattern(
        'vityl-patterns/vityl-pattern-platform',
        [
            'title'       => __('Vityl Platform', 'flynt'),
            'description' => _x('Vityl Platform', 'flynt'),
            'categories'  => ['vityl'],
            'content'     => "<!-- wp:acf/vityl-platform-hero {\"id\":\"block_61bfec2b8dce6\",\"name\":\"acf/vityl-platform-hero\",\"data\":{\"pre_title\":\"Employee engagement made easy\",\"_pre_title\":\"field_61bfeb9e5ed7d\",\"title\":\"HR’s best friend. Employees’ best advocate. Companies’ best asset.\",\"_title\":\"field_61bfeba95ed7e\",\"content\":\"Vityl is the premier employee engagement platform boosting belonging to shape winning workplaces.\",\"_content\":\"field_61bfebb35ed7f\",\"link\":{\"title\":\"Get the demo\",\"url\":\"#video\",\"target\":\"\"},\"_link\":\"field_61bfebbb5ed80\",\"image\":1716,\"_image\":\"field_61bfebc85ed81\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-advantage {\"id\":\"block_61bff915af28c\",\"name\":\"acf/vityl-advantage\",\"data\":{\"section_id\":\"vityl-advantage\",\"_section_id\":\"field_61ddad6fb8da9\",\"title\":\"The Vityl advantage.\",\"_title\":\"field_61bff8cc9aed4\",\"sub_title_1\":\"Rooted in research\",\"_sub_title_1\":\"field_61bff8de9aed5\",\"sub_content_1\":\"Informed by a scientific model of belonging validated by a study of more than 650 professionals, Vityl reflects the leading academic research on workplace belonging.\",\"_sub_content_1\":\"field_61bff8e69aed6\",\"sub_title_2\":\"Light lift, big benefits\",\"_sub_title_2\":\"field_61bff8ee9aed7\",\"sub_content_2\":\"Automatically activated surveys, goals, and nudges seamlessly foster employee engagement, growth, and connections and free HR executives from the administrative grind.\",\"_sub_content_2\":\"field_61bff8f49aed9\",\"sub_title_3\":\"Epic industry intelligence\",\"_sub_title_3\":\"field_61bff8f09aed8\",\"sub_content_3\":\"Tracking tools keep HR leaders, managers, and team members engaged and motivated, while industry-wide data helps companies understand how their culture compares.\",\"_sub_content_3\":\"field_61bff8f59aeda\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-work {\"id\":\"block_61c03d8c86592\",\"name\":\"acf/vityl-work\",\"data\":{\"section_id\":\"vityl-work\",\"_section_id\":\"field_61ddabcd31137\",\"pre_title\":\"How It Works\",\"_pre_title\":\"field_61c03d1de5e21\",\"title\":\"Building community to better businesses.\",\"_title\":\"field_61c03d25e5e22\",\"content\":\"Continuously aggregating and analyzing employee survey data, Vityl elevates the aspects of company culture that matter most to your people, prescribing organizational goals based on your team’s unique challenges and prompting motivational nudges to drive companies forward.\",\"_content\":\"field_61c03d31e5e23\",\"list_0_tab_name\":\"Surveys\",\"_list_0_tab_name\":\"field_61c03d4fe5e25\",\"list_0_tab_title\":\"Tune into your team.\",\"_list_0_tab_title\":\"field_61c03d63e5e26\",\"list_0_tab_content\":\"Vityl tracks your team’s starting line with a \\u003cstrong\\u003eBaseline Belonging and Engagement Survey,\\u003c/strong\\u003e circulates regular \\u003cstrong\\u003ePulse Surveys\\u003c/strong\\u003e to gather real-time feedback and refine goal and nudge recommendations, and consolidates user metrics into \\u003cstrong\\u003eBenchmark Data\\u003c/strong\\u003e companies can use to understand their belonging scores and gauge their own results.\",\"_list_0_tab_content\":\"field_61c03d69e5e27\",\"list_0_tab_video\":1711,\"_list_0_tab_video\":\"field_61d3c9d98b187\",\"list_1_tab_name\":\"Goals\",\"_list_1_tab_name\":\"field_61c03d4fe5e25\",\"list_1_tab_title\":\"Keep your eyes on the prize.\",\"_list_1_tab_title\":\"field_61c03d63e5e26\",\"list_1_tab_content\":\"Vityl rallies teams around a shared purpose with \\u003cstrong\\u003eGoals,\\u003c/strong\\u003e communal objectives unlocked by employee survey data. Designed to address your organization’s specific roadblocks, Goals empower tangible progress and eliminate the need for complex culture plans and playbooks.\",\"_list_1_tab_content\":\"field_61c03d69e5e27\",\"list_1_tab_video\":1712,\"_list_1_tab_video\":\"field_61d3c9d98b187\",\"list_2_tab_name\":\"Nudges\",\"_list_2_tab_name\":\"field_61c03d4fe5e25\",\"list_2_tab_title\":\"Grow company culture organically.\",\"_list_2_tab_title\":\"field_61c03d63e5e26\",\"list_2_tab_content\":\"Vityl automatically prompts AI-powered \\u003cstrong\\u003eNudges,\\u003c/strong\\u003e suggested activities and resources, based on your organization’s unique goals, drawing on machine learning to sharpen its recommendations over time. From uplifting onboarding with festive first-day agendas to mobilizing mentorship with organic outreach prompts, Vityl Nudges foster stronger bonds, thoughtful dialogue, and meaningful progress.\",\"_list_2_tab_content\":\"field_61c03d69e5e27\",\"list_2_tab_video\":1714,\"_list_2_tab_video\":\"field_61d3c9d98b187\",\"list\":3,\"_list\":\"field_61c03d40e5e24\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-discover-cta {\"id\":\"block_61c0953407879\",\"name\":\"acf/vityl-discover-cta\",\"data\":{\"title\":\"Discover Vityl.\",\"_title\":\"field_61c0949acfaad\",\"content\":\"Scope out the premier employee engagement platform to see why teams are vying for Vityl.\",\"_content\":\"field_61c094a6cfaae\",\"link_1\":{\"title\":\"Watch demo\",\"url\":\"#video\",\"target\":\"\"},\"_link_1\":\"field_61c094b2cfaaf\",\"link_2\":{\"title\":\"Browse plans\",\"url\":\"http://vityl.wunderdogs.xyz/pricing/\",\"target\":\"\"},\"_link_2\":\"field_61c094bbcfab0\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-closeup {\"id\":\"block_61c09aae7f18b\",\"name\":\"acf/vityl-closeup\",\"data\":{\"pre_title\":\"Vityl Closeup\",\"_pre_title\":\"field_61c0998853aac\",\"title\":\"Take teamwork to the top of the charts.\",\"_title\":\"field_61c0998f53aad\",\"content\":\"Include, connect, and celebrate your people with our seamless solution.\",\"_content\":\"field_61c0999353aae\",\"link\":{\"title\":\"Preview platform\",\"url\":\"#video\",\"target\":\"\"},\"_link\":\"field_61c0999953aaf\",\"image\":180,\"_image\":\"field_61c099b453ab0\"},\"align\":\"\",\"mode\":\"preview\"} /-->",
        ]
    );
    
    register_block_pattern(
