    register_block_pattern(
        'vityl-patterns/vityl-pattern-our-story',
        [
            'title'       => __('Vityl Our Story', 'flynt'),
            'description' => _x('Vityl Our Story', 'flynt'),
            'categories'  => ['vityl'],
            'content'     => "<!-- wp:acf/vityl-our-story-hero {\"id\":\"block_61c9cdc87914f\",\"name\":\"acf/vityl-our-story-hero\",\"data\":{\"title\":\"Support is Vityl.\",\"_title\":\"field_61c9b4f94a52b\",\"content\":\"Vityl is the vision of a nimble team of human capital executives and business founders passionate about giving employees the mic and leaders the tools to build thriving organizations.\\r\\n\\r\\nWith extensive experience guiding landmark companies through their trickiest culture challenges, we saw countless cumbersome products and overly complex approaches and were determined to find a better way forward.\\r\\n\\r\\nVityl is a simple means to transformative ends, combining pioneering belonging research and machine learning in an enticing, intuitive employee engagement platform that brings people together, whether they’re side by side or states apart.\",\"_content\":\"field_61c9b4fd4a52c\",\"link\":{\"title\":\"JOIN THE TEAM\",\"url\":\"http://vityl.wunderdogs.xyz/careers/\",\"target\":\"\"},\"_link\":\"field_61c9b5b301434\",\"image_1\":1738,\"_image_1\":\"field_61c9b5044a52d\",\"image_2\":1739,\"_image_2\":\"field_61c9b5124a52e\"},\"align\":\"\",\"mode\":\"preview\"} /-->\n\n<!-- wp:acf/vityl-team {\"id\":\"block_61c9cdf27916c\",\"name\":\"acf/vityl-team\",\"data\":{\"pre_title\":\"Meet The Team\",\"_pre_title\":\"field_61c9c4eba1c91\",\"team_list_0_avatar\":1650,\"_team_list_0_avatar\":\"field_61c9c573e2736\",\"team_list_0_name\":\"Yassamine Ebadat, MSW\",\"_team_list_0_name\":\"field_61c9c4fea1c93\",\"team_list_0_role\":\"Co-Founder \\u0026 Chief Executive Officer\",\"_team_list_0_role\":\"field_61c9c508a1c94\",\"team_list_0_linkedin_url\":\"https://www.linkedin.com/in/yebadat/\",\"_team_list_0_linkedin_url\":\"field_61c9c50fa1c95\",\"team_list_1_avatar\":1651,\"_team_list_1_avatar\":\"field_61c9c573e2736\",\"team_list_1_name\":\"George Ho, MHRM, SPHR\",\"_team_list_1_name\":\"field_61c9c4fea1c93\",\"team_list_1_role\":\"Co-Founder \\u0026 Chie